// Function that decrements from an available device type allocation
function decrementCount(devicesJson, type) {
  let device = devicesJson.find(el => el.type === type);
  if (device && device["count"] > 0) {
    device["count"]--
  }
  return devicesJson;
}

function getDevice(devicesJson, type) {
  return devicesJson.find(el => el.type === type);
}

function formatPhoneNumber(phoneNumberString) {
  var cleaned = ('' + phoneNumberString).replace(/\D/g, '');
  var match = cleaned.match(/^(1|)?(\d{3})(\d{3})(\d{4})$/);
  if (match) {
    var intlCode = (match[1] ? '+1 ' : '');
    return [intlCode, '(', match[2], ') ', match[3], '-', match[4]].join('');
  }
  return null;
}

module.exports = a = async ({ params }) => {
  const devices = params.devices
  const user = params.user
  const mobilePhone = user.mobilePhone

  let allowedDeviceTypes = [
    { "type": "TOTP", "count": 3, "displayLabel": "Authenticator App", "icon": "mdi-cellphone", "description": "Use an authenticator app to authenticate." },
    { "type": "PLATFORM-MAC", "count": 1, "displayLabel": "Mac Passkey", "icon": "mdi-key-chain", "description": "Authenticate using the biometrics built into your Mac device." },
    { "type": "PLATFORM-ANDROID", "count": 1, "displayLabel": "Android Passkey", "icon": "mdi-key-chain", "description": "Authenticate using the biometrics built into your Android device." },
    { "type": "PLATFORM-IOS", "count": 1, "displayLabel": "iOS Passkey", "icon": "mdi-key-chain", "description": "Authenticate using the biometrics built into your iPhone device." },
    { "type": "EMAIL", "count": 1, "displayLabel": "Email", "icon": "mdi-email-outline", "description": "Receive an email with a passcode to authenticate." },
    { "type": "SMS", "count": ((mobilePhone) ? 1 : 0), "displayLabel": "SMS", "icon": "mdi-comment-text-outline", "description": "Receive a text message with a passcode to authenticate." },
    { "type": "VOICE", "count": ((mobilePhone) ? 1 : 0), "displayLabel": "Voice", "icon": "mdi-phone", "description": "Receive a text message with a direct link to authenticate." }
  ]


  let devicesHTML = `<form id="deviceSelectionForm" data-id="deviceSelectionForm"><div class="d-flex flex-column gap-4 mb-4">`

  let i = 0
  for (const device of devices) {
    let iconClass = ""
    let displayName = ""
    let additionalDetails = ""

    if (device.type === "PLATFORM") {
      newType = `${device.type}-${device.platform}`
    } else {
      newType = device.type
    }

    let deviceMetadata = getDevice(allowedDeviceTypes, newType)

    if (deviceMetadata) {
      iconClass = deviceMetadata["icon"]

      deviceDescription = deviceMetadata["description"]

    } 

    if (device.nickname) {
      displayName  =`${deviceMetadata.displayLabel} (${device.nickname})`
    } else {
      displayName = deviceMetadata["displayLabel"]
    }

    // Decrement device from list of available devices to add
    allowedDeviceTypes = decrementCount(allowedDeviceTypes, newType)



    // Additional data to display
    switch (device.type) {
      // case 'PLATFORM':
      //   const newDeviceType = `PLATFORM-${device.platform}`
      //   iconClass = getDevice(allowedDeviceTypes, `${newDeviceType}`).icon
      //   // displayName = `Biometrics ${device.platform}`
      //   allowedDeviceTypes = decrementCount(allowedDeviceTypes, `${newDeviceType}`)
      //   break;
      case 'EMAIL':
        additionalDetails = `${device.email}`
        break;
      case 'SMS':
        additionalDetails = `${formatPhoneNumber(device.phone)}`
        break;
      case 'VOICE':
        additionalDetails = `${formatPhoneNumber(device.phone)}`
        break;
    }

    let nickname = ""
    if (device.nickname) {
      nickname = device.nickname
    }

    devicesHTML += `
    <button data-skcomponent="skbutton" class="btn btn-outline-light flex-grow-1 overrideDeviceListButtons" data-skbuttontype="form-submit" data-skform="deviceSelectionForm" data-skbuttonvalue="${device.id}" id="${i}Btn">
      <div class="d-flex align-items-center gap-4" >
        <i class="mdi ${iconClass} text-dark fs-2" aria-hidden="true"></i>
        <div class="d-flex flex-column text-start gap-1">
            <div class="fw-semibold text-primary">${displayName}</div>`
        if (additionalDetails && additionalDetails.length > 0) {
          devicesHTML += `<div class="fs-5 strong text-secondary">${additionalDetails}</div>`
        }
        devicesHTML += `
            <div class="fs-5 text-secondary">${deviceDescription}</div>

        </div>
      </div>
    </button>`
    i++
  }

  devicesHTML += `</div></form>`

  let availableDevicesHTML = `<form id="addDeviceForm" data-id="addDeviceForm"><div class="d-flex flex-column gap-4">`
   let availIndex = 0
   for (const availDevice of allowedDeviceTypes.filter(el => el.count > 0)) {
    availableDevicesHTML += `
      <button data-skcomponent="skbutton" class="btn btn-outline-light flex-grow-1 overrideDeviceListButtons" data-skbuttontype="form-submit" data-skform="deviceSelectionForm" data-skbuttonvalue="${availDevice.type}" id="btnRegister${availIndex}">
      <div class="d-flex align-items-center gap-4" >
        <i class="mdi ${availDevice.icon} text-dark fs-2" aria-hidden="true"></i>
        <div class="d-flex flex-column text-start gap-1">
            <div class="fw-semibold text-primary">Add ${availDevice.displayLabel}</div>        
            <div class="fs-5 text-secondary">${availDevice.description}</div>

        </div>
      </div>
    </button>`



    availIndex++
  }
  availableDevicesHTML += `</div></form>`


  return { 'devicesHTML': devicesHTML, 'availableDevicesHTML': availableDevicesHTML }
}

