module.exports = a = async ({params}) => {
	const deviceType = params.deviceType

	let deviceLabel = ""

	switch (deviceType) {
		case "TOTP":
			deviceLabel = "Authenticator App"
			break;
		case "EMAIL":
			deviceLabel = "Email"
			break;
		default:
			deviceLabel = deviceType
	}


	return {'deviceLabel': deviceLabel}
}

