function getDevice(devicesJson, type) {
	return devicesJson.find(el => el.type === type);
}

module.exports = a = async ({ params }) => {
	const deviceType = params.type
	const devicePlatform = params.platform

	let deviceTypes = [
		{ "type": "TOTP", "displayLabel": "Authenticator App" },
		{ "type": "PLATFORM-MAC", "displayLabel": "Mac Passkey" },
		{ "type": "PLATFORM-ANDROID", "displayLabel": "Android Passkey" },
		{ "type": "PLATFORM-IOS", "displayLabel": "iOS Passkey" },
		{ "type": "EMAIL", "displayLabel": "Email" },
		{ "type": "SMS", "displayLabel": "SMS" },
		{ "type": "VOICE", "displayLabel": "Voice" }
	]

	let deviceLookupType = deviceType


	if (deviceType === "PLATFORM") {
		deviceLookupType = `${deviceType}-${devicePlatform}`
	}

	let deviceInfo = getDevice(deviceTypes, deviceLookupType)
	let deviceLabel = deviceInfo.displayLabel

	if (!deviceLabel) {
		deviceLabel = "Unknown Device"
	}

	return { 'deviceLabel': deviceLabel }
}
