code.value:
// Write your code here
// Supported language: Javascript 
module.exports = a = async ({params}) => {
	const deviceType = params.deviceType
	let deviceFriendlyName = ""

	switch(deviceType) {
		case 'SMS':
			deviceFriendlyName = "your SMS device"
			break;
		case 'EMAIL':
			deviceFriendlyName = "your email"
			break;
		case 'VOICE':
			deviceFriendlyName = "your voice phone number"
			break;
		default:
			deviceFriendlyName = "your device"
	}
	return {'deviceFriendlyName': deviceFriendlyName}
}

